const buttons=document.querySelectorAll(".btn"),modalCloseBtns=document.querySelectorAll(".modal__close"),modalOpenBtns=document.querySelectorAll(".btn-open");buttons.forEach(t=>{const e=t.textContent;t.innerHTML=`<span>${e}</span><strong>${e}</strong>`}),modalCloseBtns.forEach(t=>{t.addEventListener("click",(function(e){const o=t.getAttribute("data-close");document.querySelector(`.modal[data-modal="${o}"]`).classList.remove("modal_active")}))}),modalOpenBtns.forEach(t=>{t.addEventListener("click",(function(e){const o=t.getAttribute("data-open");document.querySelector(`.modal[data-modal="${o}"]`).classList.add("modal_active")}))});
const cards=document.querySelectorAll(".cards__card"),cardsWrapper=document.querySelector(".cards"),audioWow=document.querySelector(".wow-audio"),modalCards=document.querySelector(".modal[data-modal='cards']"),modalCardsBody=modalCards.querySelector(".modal[data-modal='cards'] .modal__body");audioWow.volume=.05,cards.forEach((a,d)=>{a.addEventListener("click",(function(e){removeActiveClasses(),bodyBg(d),a.classList.add("cards__card_active")}))});const removeActiveClasses=()=>{cards.forEach(a=>{a.classList.remove("cards__card_active"),cardsWrapper.classList.remove("active")})},bodyBg=a=>{cardsWrapper.style.backgroundImage=`url(./img/cards/load${a}.jpg)`,cardsWrapper.classList.add("active")};modalCardsBody.addEventListener("scroll",(function(a){cardsWrapper.clientHeight-a.target.scrollTop<cardsWrapper.clientHeight-200?audioWow.classList.add("hide"):audioWow.classList.remove("hide")})),modalCards.addEventListener("click",(function(a){a.target.classList.contains("modal__close")&&audioWow.pause()}));